"use strict";const mouseMove=()=>{const title=document.getElementById("title"),bg=document.getElementById("bg");document.addEventListener("mousemove",e=>{let x=Math.round(e.pageX/100),y=Math.round(e.pageY/100);console.log(x,y),title.style.marginLeft=-x+"px",title.style.marginTop=-y+"px",bg.style.marginLeft=x+"px",bg.style.marginTop=y+"px"})};mouseMove();