"use strict";const headerTitle=()=>{const h1=document.querySelector("h1");h1.addEventListener("input",()=>{this.setAttribute("data-heading",this.innerText)})};headerTitle();const changeLang=()=>{const changelang=document.getElementById("changeLang"),clickbtn=document.getElementById("langBtn");changelang.addEventListener("change",e=>{clickbtn.click(e)})};changeLang();const pointFilter=()=>{const filter_product=document.getElementById("filter-product"),clickbtn=document.getElementById("filterBtn");filter_product.addEventListener("change",e=>{clickbtn.click(e)})};pointFilter();const scrollButton=()=>{const px_change=1;window.addEventListener("scroll",e=>{const scrollTop=window.pageYOffset||document.documentElement.scrollTop;scrollTop>1?document.getElementById("btn-backtotop").classList.add("fadein"):document.getElementById("btn-backtotop").classList.remove("fadein")}),document.getElementById("btn-backtotop").addEventListener("click",e=>(anime.remove("html, body"),anime({targets:"html, body",scrollTop:0,dulation:600,easing:"easeOutCubic"}),!1))};scrollButton();const mouseMove=()=>{const title=document.getElementById("title"),bg=document.getElementById("bg");document.addEventListener("mousemove",e=>{let x=Math.round(e.pageX/100),y=Math.round(e.pageY/100);console.log(x,y),title.style.marginLeft=-x+"px",title.style.marginTop=-y+"px",bg.style.marginLeft=x+"px",bg.style.marginTop=y+"px"})};mouseMove();const logOutConfirm=()=>{const logout=document.getElementById("logOut");logout.addEventListener("click",()=>{const answer=confirm("Are you sure logout?");answer?(window.location.href="/logout/",console.log("Logout succeeded.")):console.log("Logout cancelled.")})};logOutConfirm();