"use strict";{const num=5,winner=Math.floor(Math.random()*num);for(let i=0;i<num;i++){const div=document.createElement("div");div.classList.add("box"),div.addEventListener("click",()=>{i===winner?(div.textContent="Win!",div.classList.add("win")):(div.textContent="Lose!",div.classList.add("lose"))}),document.getElementById("counterContent").appendChild(div)}}