"use strict";{let num=3,winner=Math.floor(Math.random()*num);const counterContent=document.getElementById("counterContent"),changelevelElem=document.getElementById("counterChangeLevel"),currentValueElem=document.getElementById("counter-current-value");function setGame(num){counterContent.innerText="";for(let i=0;i<num;i++){const div=document.createElement("div");div.classList.add("box"),div.addEventListener("click",()=>{i===winner?(div.textContent="Win!",div.classList.add("win")):(div.textContent="Lose!",div.classList.add("lose"))}),counterContent.appendChild(div)}}function setCurrentValue(val){currentValueElem.innerText=val}function changeLevel(lev){setGame(1==lev?3:2==lev?4:3==lev?5:4==lev?7:10)}function rangeOnChange(e){setCurrentValue(e.target.value),changeLevel(e.target.value)}window.onload=()=>{changelevelElem.addEventListener("input",rangeOnChange),setCurrentValue(changelevelElem.value),changeLevel(changelevelElem.value)}}