"use strict";{const counterContent=document.getElementById("counterContent"),changelevelElem=document.getElementById("counterChangeLevel"),currentValueElem=document.getElementById("counter-current-value");function radomNum(num){let winner;return Math.floor(Math.random()*num)}function setGame(num){counterContent.innerText="";for(let i=0;i<num;i++){const div=document.createElement("div");div.classList.add("box");let winner=Math.floor(Math.random()*num);div.addEventListener("click",()=>{i===winner?(div.textContent="Win!",div.classList.add("win")):(div.textContent="Lose!",div.classList.add("lose"))}),counterContent.appendChild(div)}}function changeLevel(lev){1==lev?(radomNum(3),setGame(3)):2==lev?(radomNum(4),setGame(4)):3==lev?(radomNum(5),setGame(5)):4==lev?(radomNum(7),setGame(7)):(radomNum(10),setGame(10))}function setCurrentValue(val){currentValueElem.innerText=val}function rangeOnChange(e){setCurrentValue(e.target.value),changeLevel(e.target.value)}window.onload=()=>{changelevelElem.addEventListener("input",rangeOnChange),setCurrentValue(changelevelElem.value),changeLevel(changelevelElem.value)}}