"use strict";{let num=3,winner=Math.floor(Math.random()*num);const counterContent=document.getElementById("counterContent"),changelevelElem=document.getElementById("counterChangeLevel"),currentValueElem=document.getElementById("counter-current-value");function setGame(num){for(let i=0;i<num;i++){const div=document.createElement("div");div.classList.add("box"),div.addEventListener("click",()=>{i===winner?(div.textContent="Win!",div.classList.add("win")):(div.textContent="Lose!",div.classList.add("lose"))}),counterContent.appendChild(div)}}function setCurrentValue(val){currentValueElem.innerText=val}function changeLevel(lev){switch(lev){case 1:print(lev),setGame(3);break;case 2:setGame(4);break;case 3:setGame(5);break;case 4:setGame(7);break;case 5:setGame(10)}}function rangeOnChange(e){setCurrentValue(e.target.value),changeLevel(e.target.value)}setGame(3),window.onload=()=>{changelevelElem.addEventListener("input",rangeOnChange),setCurrentValue(changelevelElem.value),changeLevel(changelevelElem.value)}}