"use strict";const omikuji=()=>{const btn=document.getElementById("btn"),changelevelElem=document.getElementById("omikujiChangeLevel"),currentValueElem=document.getElementById("omikuji-current-value"),omikujiPoint=document.getElementById("omikuji_point"),omikujiPointBtn=document.getElementById("omikuji_point_btn");function setPoint(point){omikujiPoint.value=point}function sendPoint(e){omikujiPointBtn.click(e)}function setCurrentValue(val){changelevelElem.value=val,currentValueElem.innerText=val}function setGame(lev){btn.addEventListener("click",()=>{let n=Math.random();1==lev?n=n:n+=2==lev?.03:3==lev?.06:4==lev?.1:.15,n<.2?(btn.textContent="大吉",setPoint(3e3)):n<.4?(btn.textContent="吉",setPoint(1200)):n<.6?(btn.textContent="中吉",setPoint(600)):n<.8?(btn.textContent="小吉",setPoint(-200)):n<1?(btn.textContent="末吉",setPoint(-400)):(btn.textContent="凶",setPoint(-2500)),setTimeout(sendPoint,2e3)})}window.onload=()=>{let randomLev=Math.floor(5*Math.random()+1);setCurrentValue(randomLev),setGame(randomLev)}};omikuji();