"use strict";const omikuji=()=>{const btn=document.getElementById("btn"),changelevelElem=document.getElementById("omikujiChangeLevel"),currentValueElem=document.getElementById("omikuji-current-value");function setCurrentValue(val){currentValueElem.innerText=val}function setGame(lev){btn.addEventListener("click",()=>{let n=Math.random();1==lev?n=n:n+=2==lev?.03:3==lev?.06:4==lev?.1:.15,btn.textContent=n<.2?"大吉":n<.4?"吉":n<.6?"中吉":n<.8?"小吉":n<1?"末吉":"凶"})}function rangeOnChange(e){setCurrentValue(e.target.value),setGame(e.target.value)}window.onload=()=>{changelevelElem.addEventListener("input",rangeOnChange),setCurrentValue(changelevelElem.value),setGame(changelevelElem.value)}};omikuji();