"use strict";const omikuji=()=>{const btn=document.getElementById("btn"),changelevelElem=document.getElementById("omikujiChangeLevel"),currentValueElem=document.getElementById("omikuji-current-value"),omikujiPoint=document.getElementById("omikuji_point"),omikujiPointBtn=document.getElementById("omikuji_point_btn");function setPoint(point){omikujiPoint.value=point}function sendPoint(e){omikujiPointBtn.click(e)}function setCurrentValue(val){currentValueElem.innerText=val}function setGame(lev){btn.addEventListener("click",()=>{let n=Math.random();1==lev?n=n:n+=2==lev?.03:3==lev?.06:4==lev?.1:.15,n<.2?(btn.textContent="大吉",setPoint(2e3)):n<.4?(btn.textContent="吉",setPoint(1e3)):n<.6?(btn.textContent="中吉",setPoint(600)):n<.8?(btn.textContent="小吉",setPoint(400)):n<1?(btn.textContent="末吉",setPoint(200)):(btn.textContent="凶",setPoint(-1500)),setTimeout(sendPoint,2e3)})}function rangeOnChange(e){setCurrentValue(e.target.value),setGame(e.target.value)}window.onload=()=>{changelevelElem.addEventListener("input",rangeOnChange),setCurrentValue(changelevelElem.value),setGame(changelevelElem.value)}};omikuji();