"use strict";const quiz=()=>{const question=document.getElementById("quiz-question"),choices=document.getElementById("quiz-choices"),btn=document.getElementById("quiz-btn"),result=document.getElementById("quiz-result"),scoreLabel=document.querySelector("#quiz-result > p"),quizSet=shuffle([{q:"世界で一番大きな湖は？",c:["カスピ海","カリブ海","琵琶湖"]},{q:"2の8乗は？",c:["256","64","1024"]},{q:"次のうち、最初にリリースされた言語は？",c:["Python","JavaScript","HTML"]}]);let currentNum=0,isAnswered,score=0;function shuffle(arr){for(let i=arr.length-1;i>0;i--){const j=Math.floor(Math.random()*(i+1));[arr[j],arr[i]]=[arr[i],arr[j]]}return arr}function checkAnswer(li){isAnswered||(isAnswered=!0,li.textContent===quizSet[currentNum].c[0]?(li.classList.add("correct"),score++):li.classList.add("wrong"),btn.classList.remove("disabled"))}function setQuiz(){for(isAnswered=!1,question.textContent=quizSet[currentNum].q;choices.firstChild;)choices.removeChild(choices.firstChild);const shuffledChoices=shuffle([...quizSet[currentNum].c]);shuffledChoices.forEach(choice=>{const li=document.createElement("li");li.textContent=choice,li.addEventListener("click",()=>{checkAnswer(li)}),choices.appendChild(li)}),currentNum===quizSet.length-1&&(btn.textContent="Show Score")}setQuiz(),btn.addEventListener("click",()=>{btn.classList.contains("disabled")||(btn.classList.add("disabled"),currentNum===quizSet.length-1?(scoreLabel.textContent=`Score: ${score} / ${quizSet.length}`,result.classList.remove("hidden")):(currentNum++,setQuiz()))})};quiz();