const mouseMove=()=>{let title=document.getElementById("title"),bg=document.getElementById("bg");document.addEventListener("mousemove",event=>{var x=Math.round(event.pageX/100),y=Math.round(event.pageY/100);console.log(x,y),title.style.marginLeft=-x+"px",title.style.marginTop=-y+"px",bg.style.marginLeft=x+"px",bg.style.marginTop=y+"px"})};mouseMove();